mapper.select:
SELECT member_no, member_id, member_name, member_email FROM (SELECT rownum rn, member_no, member_id, member_name, member_email FROM (SELECT member_no, member_id, member_name, member_email FROM tn_ms_member
WHERE UPPER(${select}) LIKE UPPER('%'||#{keyword}||'%')
ORDER BY member_no ) ) WHERE rn BETWEEN #{rowStart} AND #{rowEnd}

mapper.update:

UPDATE 
	tn_ms_member 
SET 
	member_name=#{member_name}
	, member_email=#{member_email}
	, member_phone=#{member_phone}
	, member_subscription=#{member_subscription}
	, member_rating=#{member_rating}
	, member_marketingcheck=#{member_marketingcheck}
WHERE
	member_no = #{member_no}

mapper.select:
SELECT a.rn , a.music_no , a.album_no , a.artist_no , a.music_title , a.music_composition , a.music_write , a.music_genre , a.music_like , a.album_title , a.artist_name , b.com_code FROM (SELECT row_number() over (order by music_no) rn , a.music_no , a.album_no , a.artist_no , a.music_title , a.music_composition , a.music_write , a.music_genre , a.music_like , b.album_title , c.artist_name FROM tn_ms_music a ,tn_ms_album b ,tn_ms_artist c WHERE a.album_no = b.album_no AND a.artist_no = c.artist_no
AND UPPER(${select}) LIKE UPPER('%'||#{keyword}||'%')
) a ,tc_ms_com_code b WHERE a.rn BETWEEN #{rowStart} AND #{rowEnd}
AND a.music_genre = b.com_code_no
ORDER BY rn

mapper.update:

 	UPDATE 
		tn_ms_music
	SET 
		music_title=#{music_title}
		, music_lyrics=#{music_lyrics}
		, music_composition=#{music_composition}
		, music_write=#{music_write}
		, music_arrange=#{music_arrange}
		, music_genre=#{music_genre}
		, music_albumorder=#{music_albumorder}
		, music_adult=#{music_adult}
	WHERE
		music_no = #{music_no}


 	UPDATE 
		tn_ms_album
	SET 
		album_title=#{album_title}
		, album_type=#{album_type}
		, album_introduce=#{album_introduce}
		, album_commany=#{album_commany}
		, album_agency=#{album_agency}
		, album_date=#{album_date}
		, album_like=#{album_like}
		, album_genre=#{album_genre}
	WHERE
		album_no = #{album_no}


 	UPDATE 
		tn_ms_artist
	SET 
		artist_name=#{artist_name}
		, artist_date=#{artist_date}
		, artist_introduce=#{artist_introduce}
		, artist_company=#{artist_company}
		, artist_debut=#{artist_debut}
		, artist_awarded=#{artist_awarded}
		, artist_genre=#{artist_genre}
		, artist_fan=#{artist_fan}
	WHERE
		artist_no = #{artist_no}

mapper.insert:

INSERT INTO 
	tn_ms_music
	(
	music_no
	,album_no
	,artist_no
	,music_title
	,music_lyrics
	,music_composition
	,music_write
	,music_arrange
	,music_genre
	,music_like
	,music_playcount
	,music_adult
	)
VALUES
	(
	SEQ_MS_MUSIC.NEXTVAL
	,#{album_no}
	,#{artist_no}
	,#{music_title}
	,#{music_lyrics}
	,#{music_composition}
	,#{music_write}
	,#{music_arrange}
	,#{music_genre}
	,#{music_like}
	,#{music_playcount}
	,#{music_adult}
	)
